    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       String A,P,N;
       float Af,Pf,IMC,Acua;
        A=jTextField1.getText();
        P=jTextField3.getText();
        N=jTextField2.getText();
        Af=Float.parseFloat(A);
        Pf=Float.parseFloat(P);
        
        Acua=Af*Af;
        IMC=Pf/Acua;
        
        jTextArea1.setText("Hola "+N+ "\nSU PESO ES DE: "+P+"kg\nY SU ALTURA ES DE: "+A+"m\nPOR LO CUAL SU INDICE DE MASA CORPORAL\nES: "+IMC);
        
        if(IMC<16)
        {
            jTextField4.setText(N+" tu tienes Delgadez severa");
        }
        else if(IMC>16 && IMC<17)
        {
            jTextField4.setText(N+" tu tienes Delgadez moderada");
        }
        else if(IMC>17 && IMC<18.50)
        {
            jTextField4.setText(N+" tu tienes Delgadez aceptable");
        }
        else if(IMC>18.50 && IMC<25)
        {
            jTextField4.setText(N+" tu tienes Peso normal");
        }
        else if(IMC>25 && IMC<30)
        {
            jTextField4.setText(N+" tu tienes Sobre peso");
        }
        else if(IMC>30 && IMC<35)
        {
            jTextField4.setText(N+" tu tienes Obesidad tipo 1");
        }
        else if(IMC>35 && IMC<41)
        {
            jTextField4.setText(N+" tu tienes Obesidad tipo 2");
        }
         else if(IMC>40 && IMC<50)
        {
            jTextField4.setText(N+" tu tienes Obesidad tipo 3 (obesidad mÃ³rbida)");
        }
         else if(IMC>50)
        {
            jTextField4.setText(N+" tu tienes Obesidad tipo 4 (obesidad extrema)");
        }
       
    }//GEN-LAST:event_jButton1ActionPerformed

